import numpy as np
import math as mt

# all class and function definitions in the code file, if any
def quaternion_multiply(quaternion1, quaternion0):
    w0, x0, y0, z0 = quaternion0
    w1, x1, y1, z1 = quaternion1
    return np.array([-x1 * x0 - y1 * y0 - z1 * z0 + w1 * w0,
                     x1 * w0 + y1 * z0 - z1 * y0 + w1 * x0,
                     -x1 * z0 + y1 * w0 + z1 * x0 + w1 * y0,
                     x1 * y0 - y1 * x0 + z1 * w0 + w1 * z0])

def rot_yaw_to_quat(psi):
    return np.array([mt.cos(psi/2),0,0,mt.sin(psi/2)])

def rot_pitch_to_quat(theta):
    return np.array([mt.cos(theta/2),0,mt.sin(theta/2),0])

def rot_roll_to_quat(phi):
    return np.array([mt.cos(phi/2),mt.sin(phi/2),0,0])

def euler_to_quaternion(angles):
    roll = angles[0]
    pitch = angles[1]
    yaw = angles[2]
    q_yaw = rot_yaw_to_quat(yaw)
    q_pitch = rot_pitch_to_quat(pitch)
    q_roll = rot_roll_to_quat(roll)
    return quaternion_multiply(q_yaw, quaternion_multiply(q_pitch, q_roll))

def quaternion_to_euler(quaternion):
    a = quaternion[0]
    b = quaternion[1]
    c = quaternion[2]
    d = quaternion[3]
    return np.array([ mt.atan2(2*(a*b+c*d),1-2*(b**2 + c**2)),
                     mt.asin(2*(a*c-d*b)),
                     mt.atan2(2*(a*d+b*c),1-2*(c**2+d**2))])

# main function
def main_solution(angles):
    # Convert Euler angles to quaternion
    quaternion = euler_to_quaternion(angles)
    # Convert quaternion back to Euler angles
    euler_angles = quaternion_to_euler(quaternion)
    # Return the quaternion and the converted Euler angles
    return {"quaternion": quaternion.tolist(), "euler_angles": euler_angles.tolist()}

