def is_full1(head):
    """ 通过对比每层的节点数量，判断是不是满二叉树
    """
    if not head:
        return True
    level = 0
    node_num = 0
    queue = [[head, 0]]
    while queue:
        node, cur_lv = queue.pop(0)
        if cur_lv == level:
            node_num += 1
        else:
            if node_num != 1 << level:
                return False
            node_num = 1
            level = cur_lv
        if node.left:
            queue.append([node.left, cur_lv + 1])
        if node.right:
            queue.append([node.right, cur_lv + 1])
    return node_num == 1 << level

