import math
from copy import deepcopy

# Constants
X = "X"
O = "O"
EMPTY = None

# Function definitions from the code file
def getInitialState():
    return [[EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY]]

def getPlayer(board):
    count_x, count_o = 0, 0
    for row in board:
        count_x += row.count(X)
        count_o += row.count(O)
    if count_x > count_o:
        return O
    return X

def getActions(board):
    actions = set()
    for row_i in range(len(board)):
        for col_i in range(len(board[0])):
            if board[row_i][col_i] == EMPTY:
                actions.add((row_i, col_i))
    return actions

def getResult(board, action):
    result_board = deepcopy(board)
    player = getPlayer(board)
    result_board[action[0]][action[1]] = player
    return result_board

def getWinner(board):
    players = [X, O]
    num_symbols_in_line = 3
    for player in players:
        for row in board:
            if row.count(player) == num_symbols_in_line:
                return player
        for col_i in range(len(board[0])):
            line_count = 0
            for row_i in range(len(board)):
                if board[row_i][col_i] == player:
                    line_count += 1
            if line_count == num_symbols_in_line:
                return player
        line_count = 0
        for vert_cell in range(len(board)):
            if board[vert_cell][vert_cell] == player:
                line_count += 1
        if line_count == num_symbols_in_line:
            return player
        line_count = 0
        col_i = len(board) - 1
        for row_i in range(len(board)):
            if board[row_i][col_i] == player:
                line_count += 1
            col_i -= 1
        if line_count == num_symbols_in_line:
            return player
    return None

def isTerminal(board):
    if getWinner(board):
        return True
    count_empty = 0
    for row in board:
        count_empty += row.count(EMPTY)
    if count_empty == 0:
        return True
    return False

def getUtility(board):
    winner = getWinner(board)
    if winner == X:
        return 1
    if winner == O:
        return -1
    return 0

def minValue(board, cur_optimal_val, player):
    if isTerminal(board):
        return getUtility(board)
    val = math.inf
    for action in getActions(board):
        val = min(val, maxValue(getResult(board, action), cur_optimal_val, player))        
        if (
            (player == X and (val <= cur_optimal_val or val == -1)) or
            (player == O and val == -1)
        ):
            break
    return val

def maxValue(board, cur_optimal_val, player):
    if isTerminal(board):
        return getUtility(board)
    val = -math.inf
    for action in getActions(board):
        val = max(val, minValue(getResult(board, action), cur_optimal_val, player))
        if (
            (player == O and (val >= cur_optimal_val or val == 1)) or
            (player == X and val == 1)
        ):
            break
    return val

def minimax(board):
    optimal_action = None
    player = getPlayer(board)
    if player == X:
        optimal_val = -math.inf
        for action in getActions(board):
            val = minValue(getResult(board, action), optimal_val, player)
            if val > optimal_val:
                optimal_val = val
                optimal_action = action
                if val == 1:
                    break
    else:
        optimal_val = math.inf
        for action in getActions(board):
            val = maxValue(getResult(board, action), optimal_val, player)
            if val < optimal_val:
                optimal_val = val
                optimal_action = action
                if val == -1:
                    break
    return optimal_action

# main function
def main_solution(board):
    # Convert the input board to a list of lists if it's not already
    if isinstance(board, str):
        board = eval(board)

    # Get the optimal action for the current player on the board
    optimal_action = minimax(board)

    # Convert the output to a JSON serializable format
    return {"optimal_action": optimal_action}

