class Solution:
    def hasPath(self, matrix, rows, cols, path):
        for i in range(rows):
            for j in range(cols):
                if matrix[cols * i + j] == path[0]:  # 找到起始位置
                    if self.findpath(list(matrix), rows, cols, path[1:], i, j):
                        return True
        return False

    def findpath(self, matrix, rows, cols, path, i, j):
        if not path:
            return True
        matrix[cols * i + j] = '0'  # 标记已经走过
        if j + 1 <= cols - 1 and matrix[i * cols + j + 1] == path[0]:
            return self.findpath(matrix, rows, cols, path[1:], i, j + 1)
        elif j - 1 >= 0 and matrix[i * cols + j - 1] == path[0]:
            return self.findpath(matrix, rows, cols, path[1:], i, j - 1)
        elif i + 1 <= rows - 1 and matrix[(i + 1) * cols + j] == path[0]:
            return self.findpath(matrix, rows, cols, path[1:], i + 1, j)
        elif i - 1 >= 0 and matrix[(i - 1) * cols + j] == path[0]:
            return self.findpath(matrix, rows, cols, path[1:], i - 1, j)
        else:
            return False

