import numpy as np

# main function
def main_solution(matrix):
    # Convert the input matrix to a numpy array
    a = np.array(matrix, float)

    # Check if the matrix is symmetric and has non-negative eigenvalues
    eig_values = np.linalg.eig(a)[0]
    if not np.all(eig_values >= 0):
        return {"error": "Matrix has negative eigenvalues"}

    if not np.allclose(a, a.T):
        return {"error": "Matrix is not symmetric"}

    # Perform Cholesky decomposition
    L = np.zeros_like(a)
    n, _ = np.shape(a)
    for i in range(n):
        for j in range(i, n):
            if j == i:
                L[j, i] = np.sqrt(a[j, i] - np.sum(L[j, :i]**2))
            else:
                L[j, i] = (a[j, i] - np.sum(L[j, :i] * L[i, :i])) / L[i, i]

    # Convert the output matrix L to a list of lists for JSON serialization
    L_list = L.tolist()

    return {"L": L_list}

