def divisorGenerator(n):
    large_divisors = []
    for i in range(1, int(math.sqrt(n) + 1)):
        if n % i == 0:
            yield i
            if i*i != n:
                large_divisors.append(n / i)
    for divisor in reversed(large_divisors):
        yield divisor

def substitution(vz):
    u = (1 + math.sqrt(1 + 8 * vz)) / 2
    x = math.log2(u)
    return x

def abundant(n):
    abundant = list(divisorGenerator(n))
    total = 0
    abundant.remove(n)
    for i in abundant:
        total += i

    if total % n == 0 and total > n:
        return "hyperperfect", total
    elif total > n:
        return "abundant", total
    elif total < n:
        return "not abundant", total
    elif total == n:
        p = substitution(n)
        mersenne = 2**p - 1
        return "perfect", total, mersenne

