        numFree = 0
        testD1 = ''
        testD2 = ''
        for i in range(3):
            testD1 += self.board[i][i]
            testD2 += self.board[2 - i][i]

            testH = ''
            testV = ''
            for j in range(3):
                if self.board[i][j] == '':
                    numFree += 1

                testH += self.board[i][j]
                testV += self.board[j][i]

            if testH == 'ooo' or testV == 'ooo':
                return 'o'
            if testH == 'xxx' or testV == 'xxx':
                return 'x'

        if testD1 == 'ooo' or testD2 == 'ooo':
            return 'o'
        if testD1 == 'xxx' or testD2 == 'xxx':
            return 'x'

        if numFree == 0:
            return 'tie'

        return ''

    def getMove(self):
        while True:
            move = [random.randint(0, 2), random.randint(0, 2)]
            if self.isLegal(move):
                if self.currPlayer == 1:
                    move.append('x')
                    self.currPlayer = 2
                else:
                    move.append('o')
                    self.currPlayer = 1
                self.board[move[0]][move[1]] = move[2]
                return move

    def isLegal(self, move):
        return self.board[move[0]][move[1]] == ''

