# all class and function definitions in the code file, if any
class Queue:
    def __init__(self):
        self.q = []

    def enQ(self, data):
        self.q.append(data)

    def deQ(self):
        if self.q:
            data = self.q[0]
            self.q.remove(data)
            return data
        else:
            return None

    def isEmpty(self):
        return not bool(self.q)

class Stack:
    def __init__(self):
        self.q1 = Queue()
        self.q2 = Queue()

    def isEmpty(self):
        return self.q1.isEmpty() and self.q2.isEmpty()

    def push(self, data):
        if self.q1.isEmpty():
            self.q2.enQ(data)
        else:
            self.q1.enQ(data)

    def pop(self):
        if self.isEmpty():
            return None
        elif self.q1.isEmpty():
            while not self.q2.isEmpty():
                top = self.q2.deQ()
                if self.q2.isEmpty():
                    return top
                else:
                    self.q1.enQ(top)
        else:
            while not self.q1.isEmpty():
                top = self.q1.deQ()
                if self.q1.isEmpty():
                    return top
                else:
                    self.q2.enQ(top)

# main function
def main_solution(operations):
    stack = Stack()
    results = []

    for op in operations:
        if op[0] == 'push':
            stack.push(op[1])
        elif op[0] == 'pop':
            results.append(stack.pop())

    return results

