        for (freq, alpha) in self.liste:
            liste_feuille.append(Arbre(alpha, self.liste, freq=freq))
        self.liste_feuille = liste_feuille

    def constru_arbre(self):
        somme_freq = self.liste_feuille[0].freq + self.liste_feuille[1].freq
        self.liste.pop(1)
        self.liste.pop(0)
        noeud = Arbre('', self.liste, somme_freq, self.liste_feuille[0], self.liste_feuille[1])
        self.liste.append((noeud.freq, noeud.lettre))
        self.liste = sorted(self.liste)
        index = self.liste.index((noeud.freq, noeud.lettre))
        self.liste_feuille.pop(1)
        self.liste_feuille.pop(0)
        self.liste_feuille[index:index] = [noeud]
        return self.liste_feuille

