class ServerPlacement():

    MAX_HASH = pow(2, 32)

    def __init__(self, servers):
        self.servers = servers
        self.fraction = []
        self.ranges = []
        self.calculateFractions()

    def calculateFractions(self):
        res = [[(0, Fraction(1, 1))]]  # server index, fraction
        for i in range(1, self.servers):
            rn = []
            fraction_to_subtract = {}
            for si in range(0, i):
                fraction_to_subtract[si] = Fraction(1, i * (i + 1))
            for piece in res[i - 1]:
                si = piece[0]
                fr = piece[1]
                if (not fr > fraction_to_subtract[si]):
                    rn.append([i, fr])
                    fraction_to_subtract[si] -= fr
                else:
                    if si % 2:
                        rn.append([si, fr - fraction_to_subtract[si]])
                        rn.append([i, fraction_to_subtract[si]])
                    else:
                        rn.append([i, fraction_to_subtract[si]])
                        rn.append([si, fr - fraction_to_subtract[si]])
                    fraction_to_subtract[si] = Fraction(0, 1)
            r = []
            p = [-1, Fraction(1, 1)]
            for j in rn:
                if j[0] != p[0]:
                    r.append(j)
                else:
                    r[-1] = ([j[0], j[1] + p[1]])
                p = j
            res.append(r)
        ul = 0
        f = res[-1]
        for i in f:
            ul += i[1]
            self.ranges.append([i[0], ul])

    def getServer(self, fileHash):
        ff = Fraction(fileHash, self.MAX_HASH)
        for i in self.ranges:
            if ff < i[1]:
                return i[0]

# main function
def main_solution(servers, fileHash):
    # Convert JSON serializable inputs to original input variables
    server_placement = ServerPlacement(servers)
    # Get the server index for the given file hash
    server_index = server_placement.getServer(fileHash)
    # Return the result as a JSON serializable output
    return server_index

