import numpy as np

# main function
def main_solution(matrix_values):
    """
    Calculate the inverse of a given square matrix using Gauss-Jordan elimination.

    Parameters:
    matrix_values (list of lists of float): A square matrix represented as a list of lists.

    Returns:
    list of lists of float: The inverse of the input matrix.
    """
    x = len(matrix_values)
    m = np.zeros((x, 2 * x))

    # Fill the matrix with input values
    for i in range(x):
        for j in range(x):
            m[i][j] = matrix_values[i][j]

    # Augment the matrix with the identity matrix
    for i in range(x):
        for j in range(x):
            if i == j:
                m[i][j + x] = 1

    # Perform Gauss-Jordan elimination
    for i in range(x):
        if m[i][i] == 0.0:
            raise ValueError('Divide by zero detected!')
        for j in range(x):
            if i != j:
                ratio = m[j][i] / m[i][i]
                for k in range(2 * x):
                    m[j][k] = m[j][k] - ratio * m[i][k]

    # Normalize the diagonal elements
    for i in range(x):
        divisor = m[i][i]
        for j in range(2 * x):
            m[i][j] = m[i][j] / divisor

    # Extract the inverse matrix
    inverse_matrix = []
    for i in range(x):
        inverse_matrix.append(m[i][x:].tolist())

    return inverse_matrix

