def primeFinder(num):
    boolPrimes = [True for i in range(num + 1)]
    p = 2
    primeList = []
    while (p * p <= num):
        if (boolPrimes[p] == True):
            for i in range(p * p, num + 1, p):
                boolPrimes[i] = False
        p += 1
    for i in range(2, num + 1):
        if boolPrimes[i]:
            primeList.append(i)
    return primeList

