def maxSum(pyram, m):
    mxm = 0
    for i in range(len(pyram)):
        if mxm < max(pyram[i]):
            mxm = max(pyram[i])
    primeList = primeFinder(mxm)

    j = 0
    for i in pyram[m]:
        if i in primeList:
            pyram[m][j] = 0
        j += 1

    for i in range(m - 1, -1, -1):
        for j in range(i + 1):
            if pyram[i][j] in primeList:
                pyram[i][j] = 0
            elif (pyram[i + 1][j] > pyram[i + 1][j + 1]):
                pyram[i][j] += pyram[i + 1][j]
            else:
                pyram[i][j] += pyram[i + 1][j + 1]
    return pyram[0][0]

