class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

class Solution:
    def Convert(self, pRootOfTree):
        if not pRootOfTree:
            return None
        self.pre = None  # 必须要用全局变量，或者是传地址或引用

        self.core(pRootOfTree)
        while pRootOfTree.left:
            pRootOfTree = pRootOfTree.left
        return pRootOfTree

    def core(self, root):
        if not root:
            return None
        # 中序遍历
        if root.left:
            self.core(root.left)

        if self.pre:
            self.pre.right = root
        root.left = self.pre
        self.pre = root

        if root.right:
            self.core(root.right)

# main function
def main_solution(tree_structure):
    # Convert the JSON serializable input to the original input variables
    def build_tree(data):
        if not data:
            return None
        root = TreeNode(data[0])
        root.left = build_tree(data[1])
        root.right = build_tree(data[2])
        return root

    tree_structure = json.loads(tree_structure)
    root = build_tree(tree_structure)

    # Invoke the function that requires non-JSON serializable inputs
    solution = Solution()
    converted_tree = solution.Convert(root)

    # Convert the output to JSON serializable format
    result = []
    while converted_tree:
        result.append(converted_tree.val)
        converted_tree = converted_tree.right

    return result

