class ArbDoublyListNode:
    def __init__(self, value):
        self.value = value
        self.next = None
        self.arb = None

    def insertNodeNext(self, node):
        self.next = node

    def insertNodeArb(self, node):
        self.arb = node

# main function
def main_solution(node_values, arb_pointers):
    # Convert JSON serializable inputs to original input variables
    nodes = [ArbDoublyListNode(value) for value in node_values]

    for i in range(len(nodes) - 1):
        nodes[i].insertNodeNext(nodes[i + 1])

    for i, arb_index in enumerate(arb_pointers):
        if arb_index is not None:
            nodes[i].insertNodeArb(nodes[arb_index])

    # Clone the doubly linked list with arbitrary pointers
    def cloneDoublyArbList(root):
        if root is None:
            return None

        # Create a copy of the root node
        rootCopy = ArbDoublyListNode(root.value)
        rootCopy.next = cloneDoublyArbList(root.next)
        rootCopy.arb = root.arb

        return rootCopy

    cloned_root = cloneDoublyArbList(nodes[0])

    # Convert the cloned list to a JSON serializable output
    result = []
    current = cloned_root
    while current:
        result.append({
            "value": current.value,
            "next": current.next.value if current.next else None,
            "arb": current.arb.value if current.arb else None
        })
        current = current.next

    return result

