class Solution:
    def IsContinuous(self, numbers):
        numZero = 0
        numInterval = 0
        length = len(numbers)
        if length <= 0:
            return False
        numbers.sort()
        for i in range(0, length - 1):
            if numbers[i] == 0:
                numZero += 1
                continue
            if numbers[i] == numbers[i + 1]:
                return False
            numInterval += numbers[i + 1] - numbers[i] - 1
            if numInterval > 4:
                return False
        if numZero >= numInterval:
            return True
        return False

