import math
from itertools import count, islice

# all class and function definitions in the code file, if any
def nth(iterable, n, default=None):
    "Returns the nth item or a default value"
    return next(islice(iterable, n, None), default)

def to_coord(addr):
    dist = math.ceil((math.sqrt(addr) - 1)/2)
    width = 1 + 2 * dist

    if dist == 0:
        return (0, 0)

    start = (width - 2) ** 2 + 1
    center = start + (width - 3)//2
    from_start = addr - start
    from_center = addr - center
    side = width - 1

    if from_start < side:
        offset = from_center
        return (dist, offset)
    elif addr - start < 2 * side:
        offset = -(from_center - side)
        return (offset, dist)
    elif addr - start < 3 * side:
        offset = -(from_center - 2 * side)
        return (-dist, offset)
    else:
        offset = from_center - 3 * side
        return (offset, -dist)

def storage():
    grid = {
        (0, 0): 1
    }

    yield 1

    for addr in count(start=2):
        x, y = to_coord(addr)
        adjacent = [
            (x+1, y+0),
            (x+1, y+1),
            (x+0, y+1),
            (x-1, y+1),
            (x-1, y+0),
            (x-1, y-1),
            (x+0, y-1),
            (x+1, y-1)
        ]
        value = sum(grid.get(coord, 0) for coord in adjacent)
        grid[(x, y)] = value
        yield value

def storage_at(addr):
    return nth(storage(), addr - 1)

# main function
def main_solution(addr):
    # Convert the address to its corresponding storage value
    result = storage_at(addr)
    # Return the result as a JSON serializable output
    return result

