def main_solution(dice_count, target_sum):
    # Generate all possible outcomes for the given number of dice
    if dice_count == 2:
        outcomes = {(i, j): i + j for i in range(1, 7) for j in range(1, 7)}
    elif dice_count == 3:
        outcomes = {(i, j, k): i + j + k for i in range(1, 7) for j in range(1, 7) for k in range(1, 7)}
