import copy
import random

# all class and function definitions in the code file, if any
def curr_player(board):
    X_count = sum(row.count('x') for row in board)
    O_count = sum(row.count('o') for row in board)
    return 'x' if X_count <= O_count else 'o'

def EvaluateBoard(b):
    for a in range(3):
        if b[a][0] == b[a][1] == b[a][2] != '-':
            return b[a][0]
        if b[0][a] == b[1][a] == b[2][a] != '-':
            return b[0][a]
    if b[0][0] == b[1][1] == b[2][2] != '-' or b[0][2] == b[1][1] == b[2][0] != '-':
        return b[1][1]
    return None

def utility(board):
    winner = EvaluateBoard(board)
    return 1 if winner == 'x' else -1 if winner == 'o' else 0

def status(board):
    if EvaluateBoard(board):
        return True
    return all(cell != '-' for row in board for cell in row)

def result(board, action):
    x, y = action
    board_copy = copy.deepcopy(board)
    if board_copy[x][y] != '-':
        raise Exception("Invalid move")
    board_copy[x][y] = curr_player(board)
    return board_copy

def minvalue(board):
    if status(board):
        return utility(board), None
    best = 1000
    bestMove = None
    for i in range(3):
        for j in range(3):
            if board[i][j] == '-':
                val, _ = maxvalue(result(board, (i, j)))
                if val < best:
                    best = val
                    bestMove = (i, j)
    return best, bestMove

def maxvalue(board):
    if status(board):
        return utility(board), None
    best = -1000
    bestMove = None
    for i in range(3):
        for j in range(3):
            if board[i][j] == '-':
                val, _ = minvalue(result(board, (i, j)))
                if val > best:
                    best = val
                    bestMove = (i, j)
    return best, bestMove

# main function
def main_solution(board_str):
    board = [list(board_str[i:i+3]) for i in range(0, 9, 3)]
    if curr_player(board) == 'x':
        _, bestMove = maxvalue(board)
    else:
        _, bestMove = minvalue(board)
    return bestMove

