import math

# Function definitions from the code file
def fantion(x):
    return 4.4294 * (math.sqrt(x)) * math.tanh(1.3842 * math.sqrt(x)) - 5

def Xm(xl, xu):
    return (xl + xu) / 2.0

def Ea(xnew, xold):
    return math.fabs((xnew - xold) / xnew) * 100

# main function
def main_solution(lower, upper, max_iterations):
    # Convert inputs to appropriate types if necessary
    lower = float(lower)
    upper = float(upper)
    max_iterations = int(max_iterations)

    xm = None
    old_xm = None
    ea = None
    c = 0

    if fantion(lower) * fantion(upper) < 0:  # checking the boundary is valid
        while c < max_iterations:
            xm = Xm(lower, upper)  # Xm, estimate root
            fm = fantion(xm)
            fl = fantion(lower)

            if fm * fl > 0:
                lower = xm
            elif fm * fl < 0:
                upper = xm
            elif fm * fl == 0:
                break

            if xm and old_xm:
                ea = Ea(xm, old_xm)  # error percentage
            else:
                ea = 'N/A'

            c += 1
            old_xm = xm
    else:
        return {"error": "Boundary is not valid"}

    return {"estimated_root": xm, "error_percentage": ea}

