class LNode(object):
    def __init__(self, x):
        self.data = x
        self.Next = None

# Create a linked list from a list of values
def create_List(values):
    if type(values) != list:
        return None
    head = LNode(None)
    if not values:
        return head
    cur = head
    for v in values:
        tmp = LNode(v)
        cur.Next = tmp
        cur = tmp
    return head

# Check if two linked lists intersect
def Is_Intersect(head1, head2):
    if head1 is None or head1.data is not None or head1.Next is None:
        return False
    if head2 is None or head2.data is not None or head2.Next is None:
        return False
    entry = Loop_Entry(head1)
    if entry == False:
        return False
    cur = head2.Next
    while cur is not None:
        if cur is entry:
            break
        cur = cur.Next
    if cur is None:
        return False
    return True

# Find the loop entry of a linked list
def Loop_Entry(head):
    if head is None or head.data is not None or head.Next is None:
        return False
    lowP = head.Next
    FastP = head.Next
    while FastP is not None and FastP.Next is not None:
        lowP = lowP.Next
        FastP = FastP.Next.Next
        if lowP is FastP:
            break
    if FastP is None or FastP.Next is None:
        return False
    lowP = head.Next
    while FastP != lowP:
        lowP = lowP.Next
        FastP = FastP.Next
    return FastP

# main function
