    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    prime_list = primes(limit)
    counter = 1
    limit = len(prime_list)
    i = 0
    while i < limit:
        if prime_list[i] != 0 and check_number(prime_list[i]):
            circ = get_circular(prime_list[i])
            all_primes = True
            for x in circ:
                if x not in prime_list:
                    all_primes = False
            if all_primes:
                counter = counter + len(circ)
                for y in circ:
