class CSP_crypt:
    def __init__(self, str):
        p = str.split()
        self.p1 = p[0]
        self.p2 = p[2]
        self.p3 = p[4]
        self.opr = p[1]
        self.state = []
        self.solved = False

        for q in self.p1:
            if q not in self.state:
                self.state.append(q)
        for q in self.p2:
            if q not in self.state:
                self.state.append(q)
        for q in self.p3:
            if q not in self.state:
                self.state.append(q)

        for i in range(10 - len(self.state)):
            self.state.append('x')

    def get_number(self, p):
        num = 0
        for q in p:
            num = num * 10
            num = num + self.state.index(q)
        return num

    def solve(self):
        num1 = self.get_number(self.p1)
        num2 = self.get_number(self.p2)
        num3 = self.get_number(self.p3)

        if self.opr == '+':
            ans = num1 + num2
        elif self.opr == '-':
            ans = num1 - num2
        elif self.opr == '*':
            ans = num1 * num2
        elif self.opr == '/':
            ans = num1 / num2

        if ans == num3:
            self.solved = True

    def expand(self, l, r, depth):
        self.solve()
        if self.solved:
            return
        elif l == r:
            return
        else:
            for i in range(l, r + 1):
                self.state[l], self.state[i] = self.state[i], self.state[l]
                if self.apply_constraints(depth):
                    depth += 1
                    self.expand(l + 1, r, depth)
                    depth -= 1
                if self.solved:
                    return
                self.state[i], self.state[l] = self.state[l], self.state[i]

    def apply_constraints(self, depth):
        if (len(self.p3) > len(self.p1)) and (len(self.p3) > len(self.p2)):
            if self.state[0] == self.p3[0]:
                return True
            if self.state[1] == self.p3[0]:
                return True
            elif depth < 2:
                return True
        else:
            return True
        return False

# main function
def main_solution(problem_str):
    # Convert the input string to a CSP_crypt object
    csp_crypt = CSP_crypt(problem_str)

    # Solve the problem
    csp_crypt.expand(0, 9, 0)

    # Check if the problem is solved
    if csp_crypt.solved:
        # Convert the state to a JSON serializable format
        result = {char: csp_crypt.state.index(char) for char in csp_crypt.state if char != 'x'}
        return result
    else:
        return {"solved": False}

