def toGraph(theString):
    return [line.replace(',', ' ').replace(', ', ' ').replace('.', '').replace('\n', '') for line in theString]

def nodeList(theList):
    theString = ' '.join(theList)
    toSet = theString.split()
    result = list(set(toSet))
    result.sort()
    return result

def graphInList(theList, listOfNode):
    result = []
    for every in theList:
        tmpEvery = every.split()
        tmp = []
        for inside in tmpEvery:
            for i in range(len(listOfNode)):
                if inside == listOfNode[i]:
                    tmp.append(i)
        result.append(tmp)
    return result

def topSort(graphRepre):
    theOrder = []
    deg0 = []

    for i in range(len(graphRepre)):
        if len(graphRepre[i]) == 1:
            deg0 += graphRepre[i]

    if len(deg0) > 0:
        for j in range(len(deg0)):
            graphRepre.remove([deg0[j]])
            for every in graphRepre:
                if (len(every) > 1 and deg0[j] in every):
                    every.remove(deg0[j])
        theOrder.append(deg0)
    else:
        return None

    if (len(graphRepre) > 0):
        if (len(deg0) != 0):
            nextOrder = topSort(graphRepre)
            if (nextOrder != None):
                theOrder += nextOrder
            else:
                return None

    if (len(theOrder) > 8):
        return None

    return theOrder

# main function
def main_solution(course_data):
    # Convert the input string to a list of strings
    listFromFile = toGraph(course_data)

    # Get the list of unique nodes (courses)
    listOfNode = nodeList(listFromFile)

    # Represent the graph in a 2D list
    graphRepre = graphInList(listFromFile, listOfNode)

    # Perform topological sort
    result = topSort(graphRepre)

    # Convert the result to a JSON serializable format
    if result is None:
        return {"status": "invalid", "semesters": []}
    else:
        return {"status": "valid", "semesters": [[listOfNode[course] for course in semester] for semester in result]}

