def composite_trapezoidal(n):
    a = 0
    b = 2*np.pi
    h = (b-a)/n
    temp = 0
    for i in range(1, n):
        temp += f(h*i)
    return ((b-a)/(2*n))*(f(a)+f(b)+2*temp)

def composite_simpson(n):
    a = 0
    b = np.pi*2
    h = (b-a)/n
    temp1 = 0
    temp2 = 0
    for i in range(1, n):
        temp1 += f(h*i)
    for i in range(0, n):
        temp2 += f((0.5+i)*h)
    return ((b-a)/(6*n))*(f(a)+2*temp1+4*temp2+f(b))

