def find_eulerian_tour(graph):
    tour = []
    tour.append(graph[0][0])
    final = graph[0][1]
    graph.pop(0)
    while len(graph) > 0:
        l = len(graph)
        for i in range(l):
            if(graph[i][0] == final):
                tour.append(final)
                final = graph[i][1]
                graph.pop(i)
                break
            if(graph[i][1] == final):
                tour.append(final)
                final = graph[i][0]
                graph.pop(i)
                break
        if(len(graph) == l):
            subtour = find_eulerian_tour(graph)
            try:
                dato = tour.index(subtour[0])
                tour.pop(dato)
                for i in range(len(subtour)):
                    tour.insert(dato + i, subtour[i])
            except ValueError:
                return "No es euleriano"                  

    tour.append(final)
    if (tour[0] == tour[len(tour)-1]):
        return tour
    else:
        return "No es euleriano"

