def try_digit(n, looking_for):
    if n >= looking_for:
        return n

def get_num_digits(n=9, looking_for=500000000):
    if try_digit(n, looking_for):
        return len(str(n))
    else:
        return get_num_digits((n * 10) + 9, looking_for)

def guess_number(N, looking_for):
    msd = 0  # The digits found so far
    digits_remaining = N - 1  # The # digits remaining to the right of the one we're working on

    while digits_remaining >= 0:
        for d in range(10 * msd, 10 * msd + 10):
            try_num = (d * 10 ** digits_remaining) + (10 ** digits_remaining - 1)
            if next_digit := try_digit(try_num, looking_for):
                msd = next_digit // 10 ** digits_remaining
                break
        digits_remaining -= 1

    return msd

