class Solution:
    def canCross(self, stones: List[int]) -> bool:
        dp = {} # dp[pos] contains a set of last jump size
        for pos in stones:
            dp[pos] = set()
        dp[0].add(0)

        for pos in stones:
            for k in dp[pos]:
                for nxt in range(pos+k-1, pos+k+2):
                    if nxt > pos and nxt in dp:
