    isAlive = False
    neighbours = []
    isAliveNext = False

    def DetermineNextState(self):
        """ Determines if in the next frame of the simulation
            The cell will be alive or not. The rules are:
            Alive cells with more than 3 neighbours die
            Alive cells with less than 2 neighbours die
            Dead cells with 3 neighbours become alive """

        aliveNeighbours = sum(neighbour.isAlive for neighbour in self.neighbours)

        if self.isAlive:
            self.isAliveNext = aliveNeighbours == 2 or aliveNeighbours == 3
        else:
            self.isAliveNext = aliveNeighbours == 3

    def Advance(self):
        self.isAlive = self.isAliveNext

class Board():
    rows = 0
    columns = 0
    cells = []

    def __init__(self, rows, columns):
        self.rows = rows
        self.columns = columns
        self.cells = [[Cell() for _ in range(rows)] for _ in range(columns)]
        self.ConnectCells()

    def RandomizeCells(self, randomChance):
        for x in range(self.columns):
            for y in range(self.rows):
                self.cells[x][y].isAlive = random.randint(0, 100) < randomChance

    def ConnectCells(self):
        for x in range(self.columns):
            for y in range(self.rows):
                xL = x - 1 if x > 0 else self.columns - 1
                xR = x + 1 if x < self.columns - 1 else 0
                yT = y - 1 if y > 0 else self.rows - 1
                yB = y + 1 if y < self.rows - 1 else 0
                self.cells[x][y].neighbours = [
                    self.cells[xL][y], self.cells[xR][y],
                    self.cells[x][yT], self.cells[x][yB],
                    self.cells[xL][yT], self.cells[xR][yT],
                    self.cells[xR][yB], self.cells[xL][yB]
                ]

    def Advance(self):
        for x in range(self.columns):
            for y in range(self.rows):
                self.cells[x][y].DetermineNextState()

        for x in range(self.columns):
            for y in range(self.rows):
                self.cells[x][y].Advance()

    def GetAliveCells(self):
        return [[x, y] for x in range(self.columns) for y in range(self.rows) if self.cells[x][y].isAlive]

# main function
def main_solution(rows, columns, randomChance, steps):
    """
    Simulates the Conway's Game of Life for a given number of steps and returns the positions of alive cells after the simulation.

    Parameters:
    rows (int): Number of rows in the board.
    columns (int): Number of columns in the board.
    randomChance (int): Percentage chance for a cell to be alive initially.
    steps (int): Number of steps to simulate.

    Returns:
    list: List of lists where each sublist contains the coordinates of alive cells after the simulation.
    """
    board = Board(rows, columns)
    board.RandomizeCells(randomChance)

    for _ in range(steps):
        board.Advance()

    return board.GetAliveCells()

