import copy

# all class and function definitions in the code file, if any
def consistentAssignment(assignment, graph, var):
    neighbours = graph[var]
    for neighbour in neighbours:
        if assignment[neighbour] != -1 and assignment[neighbour] == assignment[var]:
            return False
    return True

def completeAssignment(assignment):
    for i in assignment:
        if i == -1:
            return False
    return True

def forwardChecking(assignment, domain, graph, var):
    neighbours = graph[var]
    value = assignment[var]
    for n in neighbours:
        if assignment[n] == -1:
            if value in domain[n]:
                domain[n].remove(value)
                if len(domain[n]) == 0:
                    return False
    return domain

def backtrackingWFwdChkUtility(graph, assignment, domain, var):
    if completeAssignment(assignment):
        return assignment
    for i in range(len(domain[var])):
        assignment[var] = domain[var][i]
        oldDomain = domain
        if consistentAssignment(assignment, graph, var):
            oldDomain = copy.deepcopy(domain)
            domain = forwardChecking(assignment, domain, graph, var)
            if domain != False:
                result = backtrackingWFwdChkUtility(graph, assignment, domain, var + 1)
                if result != False:
                    return result
        assignment[var] = -1
        domain = oldDomain
    return False

# main function
def main_solution(n, graph, colors):
    # Convert input variables to JSON serializable if necessary
    assignment = []
    domain = []
    for i in range(n):
        assignment.append(-1)
        domain.append(copy.deepcopy(colors))
    # Call the core function
    result = backtrackingWFwdChkUtility(graph, assignment, domain, 0)
    # Convert output to JSON serializable
    return result

