# golden section constant
phi = (math.sqrt(5) - 1) / 2

def function(x):
    return 2*x + (6/x)

def absError(xl, xu, ea):
    numIter = 1
    while True:
        err = (xu - xl) / (1.618**numIter)
        if err < ea:
            break
        else:
            numIter += 1
    return numIter

def goldenSectionMin(xl, xu, itter, itMax):
    if itter < itMax:
        x1 = xu - ((xu - xl) * phi)
        x2 = xl + ((xu - xl) * phi)
        if function(x1) > function(x2):
            return goldenSectionMin(x1, xu, itter + 1, itMax)
        else:
            return goldenSectionMin(x2, xl, itter + 1, itMax)
    return xl

# main function
def main_solution(xl, xu, ea):
    # Convert JSON serializable inputs to original input variables
    maxIter = absError(xl, xu, ea)
    # Find the minimum using the golden section method
    result = goldenSectionMin(xl, xu, 0, maxIter)
    # Convert the result to a JSON serializable output
    return result

