# import necessary packages
import math
from copy import deepcopy
from collections import deque

# Constants
X = "X"
O = "O"
EMPTY = None

# Function to initialize the board
def initial_state():
    return [[EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY]]

# Function to determine the next player
def player(board):
    X_count = sum(row.count(X) for row in board)
    O_count = sum(row.count(O) for row in board)
    return O if O_count < X_count else X

# Function to get all possible actions
def actions(board):
    return {(i, j) for i in range(3) for j in range(3) if board[i][j] == EMPTY}

# Function to get the result of an action
def result(board, action):
    current_player = player(board)
    new_board = deepcopy(board)
    if new_board[action[0]][action[1]] != EMPTY:
        raise ValueError("Invalid move")
    new_board[action[0]][action[1]] = current_player
    return new_board

# Function to determine the winner
def winner(board):
    for row in board:
        if row.count(X) == 3:
            return X
        if row.count(O) == 3:
            return O
    for col in range(3):
        if [board[row][col] for row in range(3)].count(X) == 3:
            return X
        if [board[row][col] for row in range(3)].count(O) == 3:
            return O
    if [board[i][i] for i in range(3)].count(X) == 3 or [board[i][2 - i] for i in range(3)].count(X) == 3:
        return X
    if [board[i][i] for i in range(3)].count(O) == 3 or [board[i][2 - i] for i in range(3)].count(O) == 3:
        return O
    return None

# Function to check if the game is over
def terminal(board):
    return winner(board) is not None or all(cell is not EMPTY for row in board for cell in row)

# Function to get the utility of the board
def utility(board):
    win = winner(board)
    if win == X:
        return 1
    elif win == O:
        return -1
    else:
        return 0

# Main function to solve the problem
def main_solution(board):
    if terminal(board):
        return {"winner": winner(board), "utility": utility(board)}
    else:
        return {"next_player": player(board), "possible_actions": list(actions(board))}

