def doSeq(x, DICT):
    if x == 1:
        return 1
    if x in DICT:
        return DICT[x]
    if x % 2 == 0:
        DICT[x] = 1 + doSeq(x / 2, DICT)
    else:
        DICT[x] = 1 + doSeq(3 * x + 1, DICT)
    return DICT[x]

