# all class and function definitions in the code file, if any
def factorial(n):
    assert 0 <= n == int(n), "n must be a positive integer only."
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n-1)

def fibonacci(n):
    if n in [0, 1]:
        return n
    else:
        return fibonacci(n-1) + fibonacci(n-2)

def sum_of_digits(n):
    assert n > 0, "N should be a positive integer"
    if n < 10:
        return int(n)
    else:
        return int(n % 10) + sum_of_digits(n/10)

def gcd(a, b):
    if b == 0:
        return a
    else:
        return gcd(b, a % b)

