class Solution:
    def solveNQueens(self, n: int) -> List[List[str]]:
        res = []
        if n == 0:
            return res
        columns = [-1 for _ in range(n)]
        self.dfs(n, 0, res, columns)
        return res

    def check(self, row, column, columns):
        for i in range(row):
            if column == columns[i] or row - i == abs(columns[i] - column):
                return False
        return True

    def dfs(self, n, row, res, columns):
        if row == n:
            path = ["." * i + "Q" + "." * (len(columns) - 1 - i) for i in columns]
            res.append(path)
            return
        for column in range(n):
            if self.check(row, column, columns):
                columns[row] = column
                self.dfs(n, row + 1, res, columns)
                columns[row] = -1

# main function
def main_solution(n: int) -> List[List[str]]:
    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
