import random
import math

# all class and function definitions in the code file, if any
def create(n):
    s = list(range(n*n))
    m = "<>v^"
    for i in range(n*n*n):
        if_legal(s, m[random.randrange(4)])
    return [s, ""]

def if_legal(x, m):
    n = int(math.sqrt(len(x)))
    z = x.index(0)
    if z % n > 0 and m == "<":
        x[z], x[z-1] = x[z-1], x[z]
    elif z % n < n-1 and m == ">":
        x[z], x[z+1] = x[z+1], x[z]
    elif z >= n and m == "^":
        x[z], x[z-n] = x[z-n], x[z]
    elif z < n*n-n and m == "v":
        x[z], x[z+n] = x[z+n], x[z]

def get_next(x):
    ns = []
    for i in "<>v^":
        s = x[0][:]
        if_legal(s, i)
        if s.index(0) != x[0].index(0) and (x[1] == "" or x[1][-1] != "><^v"["<>v^".index(i)]):
            ns.append([s, x[1]+i])
    return ns

def is_target(x):
    n = len(x[0])
    return x[0] == list(range(n))

# main function
def main_solution(board_size):
    # Generate a random board
    initial_state = create(board_size)

    # Check if the initial state is already the target state
    if is_target(initial_state):
        return 0

    # Initialize the queue with the initial state
    queue = [initial_state]
    visited = set()
    visited.add(tuple(initial_state[0]))

    # Perform a breadth-first search to find the shortest path to the target state
    while queue:
        current_state = queue.pop(0)
        for next_state in get_next(current_state):
            if tuple(next_state[0]) not in visited:
                if is_target(next_state):
                    return len(next_state[1])
                queue.append(next_state)
                visited.add(tuple(next_state[0]))

    # If no solution is found, return -1
    return -1

