import math
import functools
import copy

# all class and function definitions in the code file, if any
class Puzzle:
    def __init__(self, tiles):
        self._tiles = tiles
        self.tiles = copy.deepcopy(self._tiles)
        self.length = len(tiles)
        self.n = int(math.sqrt(self.length))
        self._solveable = self._solveable()
        self.fs_tiles = list(range(1, self.length))
        self.fs_tiles.append(0)

    def solveable(self):
        return self._solveable

    def _solveable(self):
        perms = self._permutations()
        if perms == 0:
            return False
        else:
            if self.n % 2 == 1:
                if perms % 2 == 0:
                    return True
                else:
                    return False
            else:
                zindex = self.tiles.index(0)
                row = (self.n) - (zindex // self.n)
                if perms % 2 == 0:
                    if row % 2 == 0:
                        return False
                    else:
                        return True
                else:
                    if row % 2 == 0:
                        return True
                    else:
                        return False

    def _permutations(self):
        i = 0
        perms = 0
        zindex = self.tiles.index(0)
        self.tiles.remove(0)

        while i < self.length - 1:
            num = self.tiles[i]
            for x in self.tiles[i:]:
                if num > x:
                    perms += 1
            i += 1

        self.tiles.insert(zindex, 0)
        return perms

    @classmethod
    def equal_tiles(cls, tiles1, tiles2):
        if functools.reduce(lambda x, y: x and y, map(lambda n1, n2: n1 == n2, tiles1, tiles2), True):
            return True
        else:
            return False

    def final_state(self):
        if Puzzle.equal_tiles(self.tiles, self.fs_tiles):
            self._solveable = True
            return True
        else:
            return False

    def _move(self, zindex, index):
        self.tiles[zindex] = self.tiles[index]
        self.tiles[index] = 0

    def move_down(self):
        zindex = self.tiles.index(0)
        index = zindex + self.n
        if index <= self.length - 1:
            self._move(zindex, index)

    def move_up(self):
        zindex = self.tiles.index(0)
        index = zindex - self.n
        if index >= 0:
            self._move(zindex, index)

    def move_right(self):
        zindex = self.tiles.index(0)
        index = zindex + 1
        if (index % self.n) != 0:
            self._move(zindex, index)

    def move_left(self):
        zindex = self.tiles.index(0)
        index = zindex - 1
        if zindex != 0:
            if (zindex % self.n) != 0:
                self._move(zindex, index)

# main function
def main_solution(tiles):
    puzzle = Puzzle(tiles)
    is_solveable = puzzle.solveable()
    return {"is_solveable": is_solveable}

