        elif not n & 1:
            return False
        else:
            s, d = 0, n - 1
            while not d & 1:
                s += 1
                d >>= 1
        for _ in repeat(None, t):
            if is_composite(randrange(2, n)):
                return False
        return True

    p = getrandbits(n)
    while not is_prime(p):
        p = getrandbits(n)
    return p

def inv(p, q):
    """Inverso multiplicativo"""
    def xgcd(x, y):
        """Algoritmo euclidiano extendido"""
        s1, s0 = 0, 1
        t1, t0 = 1, 0
        while y:
            q = x // y
            x, y = y, x % y
            s1, s0 = s0 - q * s1, s1
            t1, t0 = t0 - q * t1, t1
        return x, s0, t0

    s, t = xgcd(p, q)[0:2]
    assert s == 1
    if t < 0:
        t += q
    return t

def get_keys(p, q):
    """Gera chaves públicas e privadas"""
    phi, mod = (p - 1) * (q - 1), p * q
    if mod < 65537:
        return (3, inv(3, phi), mod)
    else:
        return (65537, inv(65537, phi), mod)

def str_2_int(text):
    """Transforma texto em números inteiros"""
    return reduce(lambda x, y: (x << 8) + y, map(ord, text))

def int_2_str(number, size):
    """Transforma inteiro em string"""
    text = "".join([chr((number >> j) & 0xff)
                    for j in reversed(range(0, size << 3, 8))])
    return text.lstrip("\x00")

def int_2_list(number, size):
    """Converte inteiro em lista de inteiros"""
    return [(number >> j) & 0xff
            for j in reversed(range(0, size << 3, 8))]

def list_2_int(listInt):
    """Converte lisa em inteiro"""
    return reduce(lambda x, y: (x << 8) + y, listInt)

def mod_size(mod):
    """Retorna o tamanho em bytes do modulo"""
    mod_size = len("{:02x}".format(mod)) // 2
    return mod_size

def encrypt(ptext, pk, mod):
    """Encripta com chave pública"""
    size = mod_size(mod)
    output = []
    while ptext:
        nbytes = min(len(ptext), size - 1)
        aux1 = str_2_int(ptext[:nbytes])
        assert aux1 < mod
        aux2 = pow(aux1, pk, mod)
        output += int_2_list(aux2, size + 2)
        ptext = ptext[size:]
    return output

def decrypt(ctext, sk, p, q):
    """Sesencripta com chave private usando o teorema chine da lembrança"""
    mod = p * q
    size = mod_size(mod)
    output = ""
    while ctext:
        aux3 = list_2_int(ctext[:size + 2])
        assert aux3 < mod
        m1 = pow(aux3, sk % (p - 1), p)
        m2 = pow(aux3, sk % (q - 1), q)
        h = (inv(q, p) * (m1 - m2)) % p
        aux4 = m2 + h * q
        output += int_2_str(aux4, size)
        ctext = ctext[size + 2:]
    return output

# main function
def main_solution(message):
    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    n = 512
    p = get_prime(n)
    q = get_prime(n)
    pk, sk, mod = get_keys(p, q)
    cipher_msg = encrypt(message, pk, mod)
    decrypted_msg = decrypt(cipher_msg, sk, p, q)
    # return, the returned value must be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    return decrypted_msg

