import math

# Function that verifies if a number is prime
def prime(x):
    if x <= 1:
        return False
    if x == 2:
        return True
    if x % 2 == 0:
        return False
    for i in range(3, int(math.sqrt(x)) + 1, 2):
        if x % i == 0:
            return False
    return True

# Function that verifies if the Goldbach Conjecture can be applied
def Goldbach(x):
    if x % 2 == 0:
        return True
    if x % 2 == 1:
        if not prime(x - 2):
            return False
        else:
            return True

# Function that finds the 2 prime numbers such as p1 + p2 = n
def GoldbachNumbers(n):
    for i in range(int(n / 2) + 1):
        if prime(i) and prime(n - i):
            return i, n - i

# main function
def main_solution(n):
    # Input argument n should be an integer
    if not Goldbach(n):
        return None  # Return None if the Goldbach Hypothesis cannot be applied
    else:
        p1, p2 = GoldbachNumbers(n)
        return {"p1": p1, "p2": p2}  # Return the prime numbers as a dictionary

