def comb(n, k, p):
    """Calculate (nCk) mod p using Fermat's Little Theorem"""
    if n < 0 or k < 0 or n < k:
        return 0
    if n == 0 or k == 0:
        return 1
    a = math.factorial(n) % p
    b = math.factorial(k) % p
    c = math.factorial(n - k) % p
    return (a * power_func(b, p - 2, p) * power_func(c, p - 2, p)) % p

def power_func(a, b, p):
    """Calculate a^b mod p using exponentiation by squaring"""
    if b == 0:
        return 1
    if b % 2 == 0:
        d = power_func(a, b // 2, p)
        return d * d % p
    if b % 2 == 1:
        return (a * power_func(a, b - 1, p)) % p

