import heapq
import random

# all class and function definitions in the code file, if any
def swap(state, start, dest):
    s = state[:]
    tmp = s[start]
    s[start] = s[dest]
    s[dest] = tmp
    return s

def succ(state):
    v = []
    i = state.index(0)
    if (i - 3) >= 0:
        v.append(swap(state, i, i - 3))
    if (i % 3) != 2:
        v.append(swap(state, i, i + 1))
    if (i + 3) <= 8:
        v.append(swap(state, i, i + 3))
    if (i % 3) != 0:
        v.append(swap(state, i, i - 1))
    return v

def manhattan_distance(p1, p2):
    return abs(p1 // 3 - p2 // 3) + abs(p1 % 3 - p2 % 3)

def distance(state):
    tot = 0
    for i in range(1, 9):
        tot += manhattan_distance(i - 1, state.index(i))
    return tot

def solve(state):
    pq = []
    visited = []
    searched = []
    heapq.heappush(pq, (distance(state), state, (0, distance(state), -1, 0)))
    ct = 1
    while pq:
        head = heapq.heappop(pq)
        visited.append(head)
        if distance(head[1]) == 0:
            return len(visited)
        successors = succ(head[1])
        for suc in successors:
            if suc not in searched:
                heapq.heappush(pq, (distance(suc) + head[2][0] + 1, suc, (head[2][0] + 1, distance(suc), head[2][3], ct)))
                searched.append(suc)
                ct += 1
    return -1

# main function
def main_solution(initial_state):
    # Convert input to the required format
    initial_state = list(initial_state)

    # Call the solve function
    result = solve(initial_state)

    # Return the result
    return result

