def euclidean(a, b):
    '''
    find x, y such that ax+by=gcd
    '''
    q = a // b
    r = a % b

    if r == 0:
        return (1, 1-q)
    (m, n) = euclidean(b, r)
    return (n, m-q*n)

def verify_euc(a, b, d, e):
    (x, y) = e
    return a * x + b * y == d

