def move_right(x, y):
    return x + 1, y

def move_down(x, y):
    return x, y - 1

def move_left(x, y):
    return x - 1, y

def move_up(x, y):
    return x, y + 1

def move_upright(x, y):
    return move_up(*move_right(x, y))

def move_upleft(x, y):
    return move_up(*move_left(x, y))

def move_downright(x, y):
    return move_down(*move_right(x, y))

def move_downleft(x, y):
    return move_down(*move_left(x, y))

def manhattan_distance(start, end):
    sx, sy = start
    ex, ey = end
    return abs(ex - sx) + abs(ey - sy)

def neigh(points, pos):
    sum = 0
    for move in diags:
        v = move(*pos)
        sum += points[v]
    return sum

moves = [move_right, move_up, move_left, move_down]
diags = moves + [move_upright, move_upleft, move_downleft, move_downright]

# main function
def main_solution(end):
    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    # if you need to invoke functions that require non-json serializable inputs, like those defined in the reference code file, you need to convert them (optional)

    def get_pos2(end):
        _moves = cycle(moves)
        points = defaultdict(int)
        n = 1
        pos = 0, 0
        m = 1  # number of times to keep going in that direction
        points[pos] = n

        while True:
            for _ in range(2):
                move = next(_moves)
                for _ in range(m):
                    if n >= end:
                        return points, pos
                    pos = move(*pos)
                    n = neigh(points, pos)
                    points[pos] = n
            m += 1
        return points, (0, 0)

    points, final_pos = get_pos2(end)
    distance = manhattan_distance((0, 0), final_pos)

    # return, the returned value must be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    return {"final_position": final_pos, "manhattan_distance": distance}

