    sortwords = [''.join(sorted(word)) for word in words]

    # Find all anagram pairs
    matchwords = []
    for i in range(len(sortwords)):
        word = sortwords[i]
        if sortwords.count(word) > 1:
            match = []
            for j, k in enumerate(sortwords):
                if k == word:
                    match.append(words[j])
            if match not in matchwords: 
                matchwords.append(match)

    # Find the largest square number formed by any member of such a pair
    max_square = 0
