class BodyT:
    def __init__(self, x, y, m):
        if len(x) != len(y) or len(x) != len(m) or len(y) != len(m):
            raise ValueError("lengths not equal")
        for i in m:
            if i <= 0:
                raise ValueError("not all masses are more than 0")
        self.__cmx = cmm(x, m)
        self.__cmy = cmm(y, m)
        self.__m = summ(m)
        self.__moment = mmomm(x, y, m) - summ(m) * (cmm(x, m)**2 + cmm(y, m)**2)

    def cm_x(self):
        return self.__cmx

    def cm_y(self):
        return self.__cmy

    def mass(self):
        return self.__m

    def m_inert(self):
        return self.__moment

def summ(m):
    summation = 0
    for i in m:
        summation += i
    return summation

def cmm(z, m):
    total = 0
    for i in range(len(m)):
        total += z[i] * m[i]
    return total / summ(m)

def mmomm(x, y, m):
    mom = 0
    for i in range(len(m)):
        mom += m[i] * ((x[i]**2) + (y[i]**2))
    return mom

