    no_of_digits = len(str(num))
    powTen = 10 ** (no_of_digits - 1)

    for i in range(no_of_digits - 1):
        first_digit = num // powTen
        left = (num * 10 + first_digit - (first_digit * powTen * 10))
        l.append(left)
        num = left

    return l

