import numpy as np
import random

# all class and function definitions in the code file, if any
def flip_move(move):
    return move[1], move[0]

def new_board(size):
    return np.zeros((2, size, size))

def make_move(board, move):
    board[0, move[0], move[1]] = 1
    return flip(board)

def flip(board):
    return np.flip(np.transpose(board, (0, 2, 1)), 0)

def symmetry(board):
    return np.flip(np.flip(board, 1), 2)

def symmetry_probs(probs):
    return np.flip(np.flip(probs, 0), 1)

def dfs(board, x, y, visited):
    size = board.shape[1]
    for dx in range(-1, 2):
        for dy in range(-1, 2):
            if dx == 0 and dy == 0 or dx * dy == 1:
                continue
            nx, ny = x + dx, y + dy
            if nx < 0 or nx >= size or ny < 0 or ny >= size:
                continue
            if board[1, nx, ny] == 0:
                continue
            if nx == size - 1:
                return True
            if (nx, ny) in visited:
                continue
            visited.add((nx, ny))
            if dfs(board, nx, ny, visited):
                return True
    return False

def winner(board):
    size = board.shape[1]
    visited = set()
    for y in range(size):
        if board[1, 0, y] == 0:
            continue
        if (0, y) in visited:
            continue
        visited.add((0, y))
        if dfs(board, 0, y, visited):
            return True
    return False

def normalize(probabilities):
    return np.copy(probabilities) / np.sum(probabilities, (0, 1))

def fix_probabilities(board, probabilities):
    size = board.shape[1]
    result = np.copy(probabilities)
    for x in range(size):
        for y in range(size):
            if board[0, x, y] != 0 or board[1, x, y] != 0:
                result[x, y] = 0
    return normalize(result)

def best_move(probabilities):
    size = probabilities.shape[0]
    max_probability = max(probabilities[x, y] for x in range(size) for y in range(size))
    return random.choice(list((x, y) for x in range(size) for y in range(size) if probabilities[x, y] == max_probability))

def sample_move(probabilities):
    size = probabilities.shape[0]
    r = random.random()
    for x in range(size):
        for y in range(size):
            prob = probabilities[x, y]
            if r < prob:
                return x, y
            r -= prob
    raise Exception('Failed to select move from probability distribution')

# main function
def main_solution(size, moves):
    # Convert moves from JSON serializable format to original format
    moves = [(move[0], move[1]) for move in moves]

    # Initialize the board
    board = new_board(size)

    # Apply each move to the board
    for move in moves:
        board = make_move(board, move)

    # Check if the second player has won
    has_won = winner(board)

    # Return the result in JSON serializable format
    return {"has_won": has_won}

