def sampling_bernoulli(p):
    res = np.random.rand()
    if res < p:
        return 1
    else:
        return 0

def sampling_binomial(n, p):
    res = 0
    for _ in range(n):
        if sampling_bernoulli(p) == 1:
            res += 1
    return res

def sampling_geometric(p):
    res = 1
    while sampling_bernoulli(p) != 1:
        res += 1
    return res

def sampling_negbinomial(n, p):
    cnt = 0
    res = 0
    while cnt < n:
        if sampling_bernoulli(p) == 1:
            cnt += 1
        res += 1
    return res

def sampling_poisson(lambd):
    return sampling_binomial(300, lambd / 300)

def sampling_exponential(lambd):
    return 0.001 * sampling_geometric(lambd * 0.001)

# main function
def main_solution(distribution_type, parameters):
    # Convert parameters to appropriate types
    parameters = {k: float(v) for k, v in parameters.items()}

    if distribution_type == "bernoulli":
        result = sampling_bernoulli(parameters["p"])
    elif distribution_type == "binomial":
        result = sampling_binomial(int(parameters["n"]), parameters["p"])
    elif distribution_type == "geometric":
        result = sampling_geometric(parameters["p"])
    elif distribution_type == "negbinomial":
        result = sampling_negbinomial(int(parameters["n"]), parameters["p"])
    elif distribution_type == "poisson":
        result = sampling_poisson(parameters["lambd"])
    elif distribution_type == "exponential":
        result = sampling_exponential(parameters["lambd"])
    else:
        raise ValueError("Invalid distribution type")

    return {"result": result}

