import random

# all class and function definitions in the code file, if any
def string_to_bytes(s):
    val = 0
    for ch in s:
        val = val << 8
        val += ord(ch)
    return val

def bytes_to_string(val):
    s = ''
    while val != 0:
        ch_code = val & 255
        s = chr(ch_code) + s
        val = val >> 8
    return s

def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)

def egcd(a, b):
    if a == 0:
        return (b, 0, 1)
    else:
        g, x, y = egcd(b % a, a)
        return (g, y - (b // a) * x, x)

def mulinv(b, n):
    g, x, _ = egcd(b, n)
    if g == 1:
        return x % n

# main function
def main_solution(text):
    # Convert the input text to bytes
    m = string_to_bytes(text)

    # Define the RSA parameters
    p = 2 ** 2203 - 1
    q = 2 ** 2281 - 1
    n = p * q
    e = 65537
    d = mulinv(e, (p - 1) * (q - 1))

    # Ensure the message is smaller than n
    while m > n:
        text = text[:-1]
        m = string_to_bytes(text)

    # Encrypt the message
    ciphertext = pow(m, e, n)

    # Decrypt the message
    decrypted_text = pow(ciphertext, d, n)

    # Convert the decrypted bytes back to string
    decrypted_message = bytes_to_string(decrypted_text)

    # Return the decrypted message
    return decrypted_message

