class Diff:
    def __init__(self, f, h=1E-5):
        self.f = f
        self.h = float(h)

class Forward1(Diff):
    def __call__(self, x):
        f, h = self.f, self.h
        return (f(x + h) - f(x)) / h

class Backward1(Diff):
    def __call__(self, x):
        f, h = self.f, self.h
        return (f(x) - f(x - h)) / h

class Backward2(Diff):
    def __call__(self, x):
        f, h = self.f, self.h
        return (f(x - 2 * h) - 4 * f(x - h) + 3 * f(x)) / (2 * h)

# main function
def main_solution(k, t, exact_derivative):
    # Convert JSON serializable inputs to the original input variables
    neg_e = lambda t: -exp(t)

    # Initialize lists to store differences
    differences_backward1 = []
    differences_backward2 = []

    # Calculate differences for each k value
    for i in range(k + 1):
        h = 2 ** (-i)
        gt2 = Backward2(neg_e, h)
        gt = Backward1(neg_e, h)
        dif2 = gt2(t) - exact_derivative
        dif = gt(t) - exact_derivative
        differences_backward1.append(dif)
        differences_backward2.append(dif2)

    # Return the differences as JSON serializable outputs
    return {
        "differences_backward1": differences_backward1,
        "differences_backward2": differences_backward2
    }

